import React from 'react';
import Users from './Users'
import './App.css';

class App extends React.Component {
  //Je déclare le state où je liste les items comme si je listais
  constructor() {
    super();
    this.state= {
        users: {},
        searchText:""
    }
  }
  componentDidMount() {
      this.getItems();
  }

  getItems=()=> {
    fetch('https://api.github.com/search/users?q='+this.state.searchText)
        .then(response => {
            console.log(response);
            /*if(response.ok) this.setState({users:[]});
            else console.log('Mauvaise réponse du réseau');*/
        })
        .catch(function(error) {
          console.log('Il y a eu un problème avec l\'opération fetch: ' + error.message);
        });
  }

  handleClick = (id) => {
    //this.clientInput.current.value;
  }

  handleSubmit = (event)=> {
    event.preventDefault();
  }

  handleChange = (event) =>{
      const value = event.currentTarget.value;
      this.setState({searchText:value});
  }

  render() {
    const title = "List of items";
    return (
        <div className="App">
            <h3>{title}</h3>
            <div>
                <form onSubmit={this.handleSubmit}>
                    <input type="search" name="" onChange={this.handleChange} placeholder="Search a user" />
                </form>
            </div>
            <div>
                {
                    this.state.users.map(user=> (
                        <Users users={user} onClick={this.handleClick}/>
                    ))
                }
            </div>
        </div>
    );
  }


}

export default App;
