import React from 'react';
import './App.css';

class App extends React.Component {
  //Je déclare le state où je liste les items comme si je listais
  constructor() {
    super();
    this.state= {
      jsonData: {}
    }
  }

  getItems = ()=> {
    fetch('https://api.github.com/users?since=135')
        .then(response => this.setState({jsonData:response.json}))
        .then(response => console.log(response))
    console.log(this.state.jsonData);
  }

  render() {
    const title = "List of items";
    return (
        <div className="App">
            <h3>{title}</h3>
            <ul className="Ul-noBullet">
                {this.getItems}
            </ul>
        </div>
    );
  }
}

export default App;
