import React from 'react';
import './App.css';

class App extends React.Component {
  //Je déclare le state où je liste les items comme si je listais
  constructor() {
    super();
    this.state= {
        jsonData: []
    }
  }
  componentDidMount() {
      this.getItems();
  }

  getItems() {
    fetch('https://api.github.com/users?since=135')
        .then(response => {
            console.log(response);
            if(response.ok) this.setState({jsonData:response});
            else console.log('Mauvaise réponse du réseau');
        })
        .catch(function(error) {
          console.log('Il y a eu un problème avec l\'opération fetch: ' + error.message);
        });
  }

  render() {
    const title = "List of items";
    return (
        <div className="App">
            <h3>{title}</h3>
            <ul className="Ul-noBullet">
            </ul>
        </div>
    );
  }
}

export default App;
