import React from 'react';
import Users from './Users'
import './App.css';

class App extends React.Component {
  //Je déclare le state où je liste les items comme si je listais
  constructor() {
    super();
    this.state= {
        users: {},
        totalCount:0,
        searchText:""
    }
  }
  componentDidMount() {
      //this.getItems();
  }

  getItems=()=> {
      console.log(this.state.searchText);
    fetch('https://api.github.com/search/users?q='+this.state.searchText)
        .then(response => response.json())
        .then(response => {
            console.log(response);
            this.setState({users:response.items});
            this.setState({totalCount: response.totalCount});
        })
        .catch(function(error) {
            console.log('Il y a eu un problème avec l\'opération fetch: ' + error.message);
        });
        /*.then(response => {

            /*if(response.ok) this.setState({users:[]});
            else console.log('Mauvaise réponse du réseau');*
        })*/

  }

  handleClick = (id) => {
    //this.clientInput.current.value;
  }

  handleSubmit = (event)=> {
    event.preventDefault();
  }

  handleChange = (event) =>{
      const value = event.currentTarget.value;
      if(value==""){
          this.setState({
              users: {},
              totalCount:0,
              searchText:""});
      }
      else{
          this.setState({searchText:value});
          this.getItems();
      }
  }

  render() {
    const title = "List of items";
    return (
        <div className="App">
            <h3>{title}</h3>
            <div>
                <form onSubmit={this.handleSubmit}>
                    <input type="search" name="" onChange={this.handleChange} placeholder="Search a user" />
                </form>
            </div>
            <div>
                {
                    Object.keys(this.state.users).map((key,index) => (
                        <Users users={this.state.users[key]} onClick={this.handleClick}/>
                    ))
                }
            </div>
        </div>
    );
  }


}

export default App;
