import React from 'react';
import Users from './Users';
import Search from './Search';
import './App.css';

class App extends React.Component {
  //Je déclare le state où je liste les items comme si je listais
  constructor() {
    super();
    this.state= {
        users: {},
        totalCount:0,
    }
  }

  getItems=()=> {
    const value = this.state.searchText;
    if(value!='')
    {
        fetch('https://api.github.com/search/users?q='+value)
            .then(response => response.json())
            .then(response => {
                console.log(response);
                this.setState({users:response.items});
                this.setState({totalCount: response.totalCount});
            })
            .catch(function(error) {
                console.log('Il y a eu un problème avec l\'opération fetch: ' + error.message);
            });
    }
  }

  render() {
    const title = "List of users";
    return (
        <div className="App">
            <h3>{title}</h3>
            <Search />
            <div className="Contains">
                {
                    Object.keys(this.state.users).map((key,index) => (
                        <Users users={this.state.users[key]}/>
                    ))
                }
            </div>
        </div>
    );
  }


}

export default App;
