import React from 'react';
import ReactDOM from 'react-dom';
import './App.css';

class App extends React.Component {

  state = {
    items: [
      {id:1, label: "Select All"},
      {id:2, label: "Item 1"},
      {id:3, label: "Item 2"},
      {id:4, label: "Item 3"},
      {id:5, label: "Item 4"},
    ],
    compteur:0
  }

  handleChange= () => {
    this.setState({compteur: this.state.compteur+1})
    console.log(this.state.compteur);
  }

  render(){
    const title = "List of items";

    return (
      <div className="App">
      <header className="App-header">
        <h3>{title}</h3>
        <ul>{this.state.items.map(item => (
            <li>
              <label>
                <input type="checkbox" name="name" onChange={this.handleChange} />
                {item.label}
              </label>
            </li>
        ))}</ul>
      </header>
    </div>
      )
  }
}



export default App;
