import React from 'react';
import ReactDOM from 'react-dom';
import './App.css';

class App extends React.Component {

  state = {
    items: {
      1: {id: 1, label: "Select All", checked: false},
      2: {id: 2, label: "Item 1", checked: false},
      3: {id: 3, label: "Item 2", checked: false},
      4: {id: 4, label: "Item 3", checked: false},
      5: {id: 5, label: "Item 4", checked: false},
    },
    compteur:0
  }

  handleChange= (id) => {
    const items = this.state.items.slice();
    if(id==1) {
      if (this.state.items[id].checked) {
        this.setState({compteur: 4});
        items.checked=true;
        //coche tout le monde;
      }
      else {
        this.setState({compteur: 0});
        items.checked=false;
        //décoche tout le monde
      }
    }
    else {
      if (this.state.items[id].checked)
        this.setState({compteur: this.state.compteur+1})
      else
        this.setState({compteur: this.state.compteur-1})

      if(this.state.compteur==4){
        items[1].checked=true;
        //coche item 1;
      }
      else {
        this.setState({compteur: 0});
        items[1].checked=false;
        //décoche item 1
      }
    }
    this.setState({items: items});


    console.log(this.state.compteur);
  }

  render(){
    const title = "List of items";

    return (
      <div className="App">
      <header className="App-header">
        <h3>{title}</h3>
        <ul>{Object.keys(this.state.items).map(item => (
            <li>
              <label>
                <input type="checkbox" name="name" onChange={()=>this.handleChange(item.id)} checked={true} />
                {item.label}
              </label>
            </li>
        ))}</ul>
      </header>
    </div>
      )
  }
}



export default App;
