import React from 'react';
import './App.css';

class App extends React.Component {
  //Je déclare le state où je liste les items comme si je listais
  state = {
    items: {
      1:{id: 1, label: "Select All", checked: false},
      2:{id: 2, label: "Item 1", checked: false},
      3:{id: 3, label: "Item 2", checked: false},
      4:{id: 4, label: "Item 3", checked: false},
      5:{id: 5, label: "Item 4", checked: false},
    }
  }

  //Fonction qui lors surgit de l'évenenement onChange des checkbox, je reçois en paramètre l'id de l'élément cliqué
  handleChange = (id) => {
    let items = this.state.items;

    //Je teste si c'est checkbox de "select All" qui a été coché
    if(id==1) {
      //Si il était déjà coché, je décoche tout le monde
      if (items[id].checked) Object.keys(items).map(function(key,index) { items[key].checked=false; });
      //sinon je coche tout le monde;
      else Object.keys(items).map(function(key,index) { items[key].checked=true; });
    }
    else {
      //Si c'est un autre qui a été coché, je teste si il était déjà coché, je le décoche, sinon je le coche
      if (items[id].checked) items[id].checked = false;
      else items[id].checked = true;

      //Je teste si tous les éléments sont cochés, si oui k=je coche le "select all", sinon je le décoche
      const itemsLength = Object.keys(items).length-1;
      let checkedNumber = 0;
      Object.keys(this.state.items).map((key,index) => {
        checkedNumber+= (items[key].checked) ? 1 : 0;
      });
      if(checkedNumber >= itemsLength) items[1].checked = true;
      else items[1].checked = false;

      console.log("itemsLenght="+itemsLength);
      console.log("checkedNumber="+checkedNumber);
    }

    //je mets à jour le composant
    this.setState({items: items});
    console.log(this.state.items);
  }

  render(){
    const title = "List of items";

    return (
      <div className="App">
        <header className="App-header">
          <h3>{title}</h3>
          <ul>/*Je fais une boucle qui va afficher tous les éléments de mon state*/
            {
              Object.keys(this.state.items).map((key,index) => (
              <li>
                <label>
                  <input type="checkbox" name="name" onChange={()=>this.handleChange(this.state.items[key].id)} checked={this.state.items[key].checked} />
                  {this.state.items[key].label}
                </label>
              </li>
            ))}
          </ul>
        </header>
      </div>
      )
  }
}



export default App;
