import React from 'react';
import ReactDOM from 'react-dom';
import './App.css';

class App extends React.Component {

  state = {
    items: [
      {id:1, label: "Select All"},
      {id:2, label: "Item 1"},
      {id:3, label: "Item 2"},
      {id:4, label: "Item 3"},
      {id:5, label: "Item 4"},
    ],
    compteur:0
  }

  render(){
    const title = "List of items";
    const elements = this.state.items.map(function(item){
      return <li><label>{item.label} <input type="checkbox" name="name{item.id}" /></label></li>
    })
    return (
      <div className="App">
      <header className="App-header">
        <form>
          <label>
            Select all
            <input type="checkbox" name="name" onChange={this.handleClick}/>
          </label><br/>
          <label>
            Item 1
            <input type="checkbox" name="name" onChange={this.hasChange}/>
          </label><br/>
          <label>
            Item 2
            <input type="checkbox" name="name" onChange={this.hasChange}/>
          </label><br/>
          <label>
            Item 3
            <input type="checkbox" name="name" onChange={this.hasChange}/>
          </label><br/>
          <label>
            Item 4
            <input type="checkbox" name="name" onChange={this.hasChange}/>
          </label><br/>

        </form>
      </header>
    </div>
      )
  }
}



export default App;
